package euler;


public class ProblemEight {

	private static String string = "";
	static char[] string2 = string.toCharArray();

	public static void main(String args[]) {
		final long startTime = System.currentTimeMillis();
		doCalculation();
		final long total = System.currentTimeMillis() - startTime;
		System.out.println(total);
	}

	public static void doCalculation() {

		int x = string2.length;
		int total = 0;
		int y = 0, b =0 ;
		int temp = 1;

		for (y = 0; y <= x; y++) {
			temp = 1;
			if (y <= (x - 6)) {
				for (int z = 0; z <= 4; z++) {
					int now = Character.getNumericValue(string2[y + z]);
					if (now >= 0) {
						temp *= Character.getNumericValue(string2[y + z]);
					}
				}

				if (temp > total) {
					b = y;
					total = temp;
				}
			}
		}
		System.out.println("at " + b + " numbers are: " +  string2[b] + string2[b+1] + string2[b+2] + string2[b+3] + string2[b+4]);
		System.out.println("total is: " + total);
	}
}
